svg.script:

function bootSequence() {
  const lines = document.querySelectorAll('.line');
  const loadBar = document.getElementById('loadbar');
  const ready = document.getElementById('ready');
  const fail1 = document.getElementById('fail1');
  const fail2 = document.getElementById('fail2');

  // Reset everything
  lines.forEach(line => line.style.opacity = '0');
  loadBar.setAttribute('width', '0');
  ready.style.opacity = '0';
  fail1.style.opacity = '0';
  fail2.style.opacity = '0';

  // Animate lines
  lines.forEach((line, index) => {
    setTimeout(() => {
      line.style.opacity = '1';
      line.style.transition = 'opacity 0.3s';
    }, index * 1000);
  });

  // Animate loading bar
  setTimeout(() => {
    loadBar.style.transition = 'width 4s linear';
    loadBar.setAttribute('width', '300');
  }, 9000);

  // Show ready message
  setTimeout(() => {
    ready.style.opacity = '1';
    ready.style.transition = 'opacity 0.3s';
  }, 13000);

  // Flash failure messages
  let flashCount = 0;
  const failInterval = setInterval(() => {
    fail1.style.opacity = fail1.style.opacity === '1' ? '0' : '1';
    fail2.style.opacity = fail2.style.opacity === '1' ? '0' : '1';
    flashCount++;
    if (flashCount > 10) {
      clearInterval(failInterval);
      // Restart the sequence after a pause
      setTimeout(bootSequence, 2000);
    }
  }, 500);
  setTimeout(() => {
    fail1.style.opacity = '1';
    fail2.style.opacity = '1';
  }, 14000);
}

// Start the animation
bootSequence();
